import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import instance from "../../api/api"; // 이미 axios 인스턴스 설정되어 있다고 가정
import { useSelector } from "react-redux"; // 로그인 상태를 Redux로 관리한다고 가정

const Review = () => {
  const [title, setTitle] = useState(""); // 리뷰 제목
  const [content, setContent] = useState(""); // 리뷰 내용
  const [error, setError] = useState(""); // 에러 메시지
  const [loading, setLoading] = useState(false); // 요청 로딩 상태
  const navigate = useNavigate();

  // Redux에서 로그인한 사용자 정보 가져오기 (예: 사용자 토큰, ID 등)
  const user = useSelector((state) => state.user); // 로그인한 사용자 정보 (redux store 사용 예시)

  useEffect(() => {
    // 사용자가 로그인하지 않은 경우 로그인 페이지로 리다이렉트
    if (!user || !user.token) {
      navigate("/user/login");
    }
  }, [user, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !content) {
      setError("제목과 내용을 모두 작성해주세요.");
      return;
    }

    setLoading(true);
    try {
      const response = await instance.post(
        "/reviews", // 리뷰를 저장할 API 엔드포인트
        {
          title,
          content,
          userId: user.id, // 로그인한 사용자 정보와 함께 전송
        }
      );

      // 성공 시, 리뷰 등록 완료 메시지
      if (response.status === 201) {
        alert("리뷰가 등록되었습니다.");
        setTitle("");
        setContent("");
      }
    } catch (err) {
      console.error(err);
      setError("리뷰 등록 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>리뷰 작성</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">제목</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="리뷰 제목을 입력하세요"
            required
          />
        </div>
        <div>
          <label htmlFor="content">내용</label>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="리뷰 내용을 입력하세요"
            required
          />
        </div>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <button type="submit" disabled={loading}>
          {loading ? "등록 중..." : "리뷰 등록"}
        </button>
      </form>
    </div>
  );
};

export default Review;
